select
	temp.desechable
	, count(*)
from (
	select
		p.name
		, case
			when (
				lower(p.name) like '%test%'
				or lower(p.name) like '%trial%'
				or lower(p.name) like '%sample%'
			) then 'yes'
			else 'no'
		end as 'disposable'
	from positions as p
	inner join positions_side_owners as pso on pso.position_id = p.id
	where 1=1
		and pso.is_owner
		and pso.caliber_id in (
			select
				cc.caliber_id
			from customer_companies as cc
			where 1=1
				and cc.company_id in (1480384, 1152862, 122570, 336969, 1585720, 1220150, 58335, 623235, 1017399, 192202, 1434273, 24593, 993, 6831, 1630666, 1360705, 1082598, 23060, 1115800, 453752, 897956, 1676755, 296929, 1453961, 217275, 29306, 288783, 72568, 647242, 399415, 1021, 418844, 573347, 590895, 456295, 2783242, 10044, 1010469, 91437, 520, 12689, 1400225, 315669, 707032, 197476, 17673, 32996, 824315, 351181, 160397, 50163, 371214, 1698068, 1179140, 127808, 6769, 731623, 924821, 1068162, 103782, 775112, 233085, 126120, 2806955, 273017, 60354, 481199, 78190, 214809, 696189, 621695, 1369309, 64524, 16774, 391147, 212560, 92266, 629, 566392, 330335, 2780978, 1260082, 937934, 1410996, 206840, 21375, 1113185, 1503602, 126678, 581191, 364445, 1579441, 457769, 1658100, 942664, 94322, 691122, 417278, 131598, 455392, 1263366, 158376, 767684, 612213, 502360, 90251, 533544, 1115045, 233163, 61092, 1095888, 1629)
		)
) as temp
group by
	temp.desechable